import React, {Component} from 'react';

import Header from './Header'; // Import a component from another file
import Board from './Board'; // Import a component from another file

const my_name = "Sinan cengiz"
const last_name = "Cengiz"

class Game extends Component {

  state={
    form_hide:"hiden"
  };

  start_game(){
    console.log("start button cliked");
    this.setState(
      {
        form_hide:"inline"
      }
    ) 
  }

  render(){
    return (
      <div className="container">
        <div>
          <Header></Header>
        </div>
        <div>
        <button onClick={this.start_game.bind(this)}>Start</button>
        </div>
        <div className={this.state.form_hide}>
          <h1>this is form</h1>
        </div>
        <div>
        <Board/>
        </div>
        

      </div>

     );
  }
}

export default Game;
