import React, {Component} from 'react';

import Header from './Header'; // Import a component from another file
import Board from './Board'; // Import a component from another file
import Info from './Info'; // Import a component from another file


class Game extends Component {

  state={
    form_hide:"hiden",
    board_hide:"hiden",
    button_hide:"block",
    player_X:"",
    player_O:"",
    game_board:[[0,0,0],[0,0,0],[0,0,0]]
  };

  start_button = () => {
    this.setState( prevState => (
      {
        form_hide:"block",
        button_hide:"hiden"
      }
   )
  ) 
  }

  start_game_button(){
    console.log("start button cliked");
    this.setState(
      {
        form_hide:"hiden",
        board_hide:"block",
      }
    ) 
  }

  new_game_button(){
    console.log("start button cliked");
    this.setState(
      {
        form_hide:"hiden",
        board_hide:"hiden",
        button_hide:"block",
      }
    ) 
  }

  reset_game_button(){
    console.log("start button cliked");
    this.setState(
      {
        form_hide:"hiden",
        board_hide:"block",
      }
    ) 
  }

  handle_players_name = (player_X) => {
    this.setState( prevState => {
      return {
        player_X: prevState.player_X = player_X,
      };
    });
  }


  render(){
    return (
      <div className="container">
        <div>
          <Header></Header>
        </div>
        <div className={this.state.button_hide} >
          <button  onClick={this.start_button.bind(this)}>Start</button>
        </div>
        <div className={this.state.form_hide}>
            <Info 
            player_X={this.state.player_X}
            player_O={this.state.player_O}
            handle_players_name = {this.handle_players_name}
            />
            <button  onClick={this.start_game_button.bind(this)}>Start Game</button>
        </div>
        <div className={this.state.board_hide}>
          <h1>{this.state.player_X}</h1>
          <h1>{this.state.player_O}</h1>

        </div>
        <div className={this.state.board_hide}>
          <p></p>
          <Board />
          <button  onClick={this.new_game_button.bind(this)}>New Game</button>
          <button  onClick={this.reset_game_button.bind(this)}>Reset Game</button>
        
        </div>
        

      </div>

     );
  }
}

export default Game;
