import React, {Component}  from 'react';
import x_image from '../images/1.png'; 
import o_image from '../images/0.png'; 

class  Cell extends Component {

state={
  value:"none"
}

handle_cell_click = () =>{
  console.log("cells clicked")
  
}  

render(){
  let img= <img/>;

  if (this.state.value = "none") {
    img= <img/>;
  } else if(this.state.value = "player_X") {
    img= <img className="cell" src={x_image} alt="o_image"/>
  }else{
    img= <img className="cell" src={o_image} alt="o_image"/>
  }

  return(
    <div className="cell" onClick={this.handle_cell_click}>
        img

    </div>

  );
  }

}

export default Cell;
