import React, {Component} from 'react';
import x_image from '../images/1.png'; 
import o_image from '../images/0.png'; 

class Info extends Component {

  state ={
    player_X:"",
    player_O:""
  }

  handle_value_change = (e) => {
    this.setState({ player_X: e.target.value});
  }

  handle_submit = (e) => {
    e.preventDefault();
    this.props.handle_players_name(this.state.player_X)
  }

  render(){
    return(
      <form onSubmit={this.handle_submit}>

        <input 
        type="text"
        value={this.state.player_X}
        onChange={this.handle_value_change}
        placeholder="Enter Player name"
        />

        <input
        type="submit"
        value="Start game"
        />

      </form>

    );

  }


}


export default Info;
