import React, {Component} from 'react';
import x_image from '../images/1.png'; 
import o_image from '../images/0.png'; 

class Info extends Component{

  state={
    player_x:"",
    player_o:""
  }

  handle_x_Name = () => {
    this.setState( prevState => (
      {
        player_x:event.target.value,
      }
    )
    ) 
  }

  render(){
    return(

      <div>
        <h2>this is the from element</h2>
        <label>
          Name  
          <img className="info_form_image" src={x_image} alt="o_image" />
          <input type="text" name="name" onChange={this.handle_x_Name.bind(this)}/>
        </label>
        

        
        <label>
          Name 
          <img className="info_form_image" src={o_image} alt="o_image" />
          
          <input type="text" name="name" />
        </label>


      </div>
    )
  }
}


export default Info;
