import React, {Component} from 'react';

import Header from './Header'; // Import a component from another file
import Board from './Board'; // Import a component from another file
import Info from './Info'; // Import a component from another file


class Game extends Component {

  state={
    form_hide:"hiden",
    board_hide:"hiden",
    button_hide:"block",
    player_X:"",
    player_O:"",
    turn:"player_X",
    game_board:[ [
      {
      cell_id:0,
      cell_value:"none",
      cell_coordinate:[0,0]
    },
    {
      cell_id:1,
      cell_value:"none",
      cell_coordinate:[0,1]
    },
    {
      cell_id:2,
      cell_value:"none",
      cell_coordinate:[0,2]
    }],
    [{
      cell_id:3,
      cell_value:"none",
      cell_coordinate:[1,0]
    },
    {
      cell_id:4,
      cell_value:"none",
      cell_coordinate:[1,1]
    },
    {
      cell_id:5,
      cell_value:"none",
      cell_coordinate:[1,2]
    }],
    [{
      cell_id:6,
      cell_value:"none",
      cell_coordinate:[2,0]
    },
    {
      cell_id:7,
      cell_value:"none",
      cell_coordinate:[2,1]
    },
    {
      cell_id:8,
      cell_value:"none",
      cell_coordinate:[2,2]
    }]
  ]

  };

  start_button = () => {
    this.setState( prevState => (
      {
        form_hide:"block",
        button_hide:"hiden"
      }
   )
  ) 
  }

  new_game_button = () => {
    console.log("start button cliked");
    this.setState(
      {
        form_hide:"block",
        board_hide:"hiden",
        button_hide:"hiden",
        player_X:"",
        player_O:"",
        turn:"player_X",
        game_board:[ [
          {
          cell_id:0,
          cell_value:"none",
          cell_coordinate:[0,0]
        },
        {
          cell_id:1,
          cell_value:"none",
          cell_coordinate:[0,1]
        },
        {
          cell_id:2,
          cell_value:"none",
          cell_coordinate:[0,2]
        }],
        [{
          cell_id:3,
          cell_value:"none",
          cell_coordinate:[1,0]
        },
        {
          cell_id:4,
          cell_value:"none",
          cell_coordinate:[1,1]
        },
        {
          cell_id:5,
          cell_value:"none",
          cell_coordinate:[1,2]
        }],
        [{
          cell_id:6,
          cell_value:"none",
          cell_coordinate:[2,0]
        },
        {
          cell_id:7,
          cell_value:"none",
          cell_coordinate:[2,1]
        },
        {
          cell_id:8,
          cell_value:"none",
          cell_coordinate:[2,2]
        }]
      ]
      }
    ) 
  }

  reset_game_button = () => {
    console.log("start button cliked");
    this.setState(
      {
        form_hide:"hiden",
        board_hide:"block",
        button_hide:"hiden",
        turn:"player_X",
        game_board:[ [
          {
          cell_id:0,
          cell_value:"none",
          cell_coordinate:[0,0]
        },
        {
          cell_id:1,
          cell_value:"none",
          cell_coordinate:[0,1]
        },
        {
          cell_id:2,
          cell_value:"none",
          cell_coordinate:[0,2]
        }],
        [{
          cell_id:3,
          cell_value:"none",
          cell_coordinate:[1,0]
        },
        {
          cell_id:4,
          cell_value:"none",
          cell_coordinate:[1,1]
        },
        {
          cell_id:5,
          cell_value:"none",
          cell_coordinate:[1,2]
        }],
        [{
          cell_id:6,
          cell_value:"none",
          cell_coordinate:[2,0]
        },
        {
          cell_id:7,
          cell_value:"none",
          cell_coordinate:[2,1]
        },
        {
          cell_id:8,
          cell_value:"none",
          cell_coordinate:[2,2]
        }]
      ]
      }
    ) 
  }

  handle_players_name = (player_X, player_O) => {
    this.setState( prevState => {
      return {
        player_X: prevState.player_X = player_X,
        player_O: prevState.player_O = player_O,
        form_hide:"hiden",
        board_hide:"block",
      };
    });
  }

  handle_cell_click = (id,cell_coordinate) => {
    this.setState( prevState => {
      if(prevState.game_board[cell_coordinate[0]][cell_coordinate[1]].cell_value == "none"){
        console.log(prevState.turn)
        console.log(prevState.game_board[cell_coordinate[0]][cell_coordinate[1]].cell_value)
        prevState.game_board[cell_coordinate[0]][cell_coordinate[1]].cell_value = prevState.turn
        if(prevState.turn == "player_X"){
          prevState.turn= "player_O"
        }else{
          prevState.turn= "player_X"
        }
        
        console.log(this.state.game_board)
      }

      
      return {
        form_hide:"hiden",
        board_hide:"block",
        
      };
    });
  }
      


  render(){
    return (
      <div className="container">
        <div>
          <Header></Header>
        </div>
        <div className={this.state.button_hide} >
          <button  onClick={this.start_button.bind(this)}>Start</button>
        </div>
        <div className={this.state.form_hide}>
            <Info 
            handle_players_name = {this.handle_players_name}
            />
        </div>
        <div className={this.state.board_hide}>
          <h1>{this.state.player_X}</h1>
          <h1>{this.state.player_O}</h1>

        </div>
        <div className={this.state.board_hide}>
          <p></p>
          <Board 
          game_board = {this.state.game_board} 
          turn ={this.state.turn}
          handle_cell_click = {this.handle_cell_click}
          />
          <button  onClick={this.new_game_button.bind(this)}>New Game</button>
          <button  onClick={this.reset_game_button.bind(this)}>Reset Game</button>
        
        </div>
        

      </div>

     );
  }
}

export default Game;
